// GStreamer error handling callback
static void handle_gstreamer_error(GstBus *bus, GstMessage *msg)
{
    GError *err = NULL;
    gchar *debug_info = NULL;
    gst_message_parse_error(msg, &err, &debug_info);
    fprintf(stderr, "GStreamer Error: %s (%s)\n", err->message, debug_info ? debug_info : "no debug info");
    g_clear_error(&err);
    g_free(debug_info);
}

