    g_object_set(G_OBJECT(capsfilter), "caps", caps, NULL);
    gst_caps_unref(caps);

    // Configure kmssink
    g_object_set(G_OBJECT(sink), "driver-name", "tidss", "render-rectangle", "{0,0,800,450}", NULL);

